                                # Ex 01 :  Taille tableau  #

def ask_length(tableau)
    print "01. Ceci est le nombre de journaliste présents dans le tableau : "
    puts tableau.length
    puts "~" * 25
    return tableau.length
end

                           # Ex 02 : Nom le plus court du tableau #

def ask_shortest(tableau)
    print "02. Ceci est l'élément le plus court du tableau : "
    puts tableau.min_by {|element| element.length}
    puts "~" * 25
    return tableau.min_by
end

                           # Ex 03 : Nombre de noms à 5 caractères #

def count_five(tableau)
    print "03. Ceci est le nombre d'éléments à 5 caractères : "
    puts tableau.count {|element| element.length == 6}
    puts "~" * 25
    return tableau.count
end

                           # Ex 04 : Nombre de noms à majuscule #

def count_uppercase(tableau)
    count = 0
    tableau.each do |element|
        count += 1 if element.match /@[A-Z]/ # compte les element avec 1 maj + 1 @ 
    end
    print "04. Ceci est le nombre de journalistes ayant une majuscule après leur @ : "
    puts count
    puts "~" * 25
    return count
end

                           # Ex 05 : Ordre alphabétique #

def alphabetical_order(tableau)
    print "05. Ceci est le tableau trié de manière alphabetique : "
    puts tableau.sort
    puts "~" * 25
    return tableau.sort
end

                           # Ex 06 : Ordre croissant #

def ascending_order(tableau)
    print "06. Ceci est le tableau trié de manière croissante : "
    puts tableau.sort_by(&:length)
    puts "~" * 25
    return tableau.sort_by(&:length)
end

                           # Ex 07 : Position @Epenser #

def position_epenser(tableau)
    print "07. Ceci est la position de @epenser dans le tableau : "
    index = tableau.index("@epenser")
    puts index
    puts "~" * 25
end

                           # Ex 08 : Répartition par taille  #

def repartition(tableau)
    print "08. Ceci est la répartition des éléments par taille"
    counts = {}
    tableau.each do |element|
    length = element.length - 1
    counts[length] ||= 0
    counts[length] +=1
    end
    counts.each do |length, count|
    puts "#{count} handle(s) avec #{length} caractere(s)"
    end
    
    puts "~" * 25
end

def perform
    ask_length(tableau)
    ask_shortest(tableau)
    count_five(tableau)
    count_uppercase(tableau)
    alphabetical_order(tableau)
    ascending_order(tableau)
    position_epenser(tableau)
    repartition(tableau)
end

perform